import './App.scss';
import React, {Component} from 'react';
import {BrowserRouter, Route, Switch, Redirect, Link, withRouter} from "react-router-dom"
import Header from "./components/Header/Header";
import VideoList from "./components/VideoList/VideoList";
import Data from "./data/videos.json";
import DataDetails from "./data/video-details.json";
import MainVideoDisplay from "./components/MainVideoDisplay/MainVideoDisplay"
import Upload from "../src/components/Upload/Upload";
import VideoContainer from "../src/components/VideoContainer/VideoContainer"
import {API_KEY, API_URL} from "../src/utils/api";
import axios from "axios";


class App extends Component{


  setCurrentVideo = (id) => {
    const individualEntry = this.state.find((video) => video.id === id);
    return this.setState({ mainEntry: individualEntry});
  };

videoDisplay = (event) => {

  event.preventDefault();
  let image = this.state.image;
  let title = this.state.title;
  let channel = this.state.channel;
  let id = this.state.id;
  let description = this.state.description;
  let likes = this.state.likes;

  let timestamp= this.state.timestamp;
  let views = this.state.views;
  let video = this.state.video;
  let newVideo = { id: id, title: title, image: image, channel: channel, description: description, likes: likes, timestamp:timestamp, views: views, video: video};
  let newArray = this.state.videos.concat(newVideo);
  this.setState({ videoDetail: newArray });
  event.target.reset();
};

  render(){

  return (
    <div className="App">
      <BrowserRouter>
        <Header />
        <Switch>
          <Route exact path="/upload" component={Upload}/>

          <Route exact path="/" component={MainVideoDisplay}/>

          <Route exact path="/:videoId" component={MainVideoDisplay}/>
{/* 
          <Route exact path="/" component={withRouter (VideoContainer)}/>

          <Route exact path="/:videoId" component={withRouter (VideoContainer)}/> */}

        </Switch>
      </BrowserRouter>
    </div>
  )}
}

export default App;
