import React from 'react';
import getDate from "../GetDate/GetDate"
import appStyle from "../../App.scss"
import CommentDisplayStyle from "../CommentDisplay/CommentDisplay.scss"


function CommentDisplay (props){
        
    return (
        <div>
            {props.entry.comments.map((comment) =>(
                <ul className="comment__style">
                    <li key={comment.id} className="comment-name__style"
                        > <img className="card__photo"/>{comment.name}<span className="comment-date__style">{getDate(comment.timestamp)} ago</span></li>
                    <li className="comment-body__style">        {comment.comment}</li>  
                </ul>
            ))}
        </div>
    )
}

export default CommentDisplay
