import React from 'react';
import VideoDisplay from "../VideoDisplay/VideoDisplay"
import VideoListStyle from "../VideoList/VideoList.scss"


function VideoList(props){

  return (
    
      <section className="videolist-container__style">
        <div className="videolist__content">
        <h2 className="nextvideo-title__style">NEXT VIDEO</h2>
        <ul className="videolist__style">{props.videoDetail.map((video) => {return (
          <div className="nextvideo__container">
                <div className="nextvideo__flex">
                    <video width= "600px" height="100" className="nextvideo__style" poster={video.image}>
                        <source className="video__style" src={video.video}/>
                    </video>
                    <div className="nextvideo-list-container">
                        <li className="nextvideo-desc__style"
                          key={video.id}onClick={() => props.setCurrentVideo(video.id)}>{video.title}
                        </li>
                        <li className="nextvideo-channel__style">
                          {video.channel}
                        </li>
                    </div>
                  </div>
              </div>
               
            );
          })}
        </ul>
        </div>
      </section>
  )}

export default VideoList
