import React, {Component} from 'react';
import {NavLink, withRouter} from "react-router-dom"
import "../../App.scss"
import "../VideoList/VideoList.scss"
import {API_KEY, API_URL} from "../../utils/api";
import axios from "axios"
import MainVideoDisplay from "../MainVideoDisplay/MainVideoDisplay"

class VideoList extends Component{
  
  state = {
    videoList: [],
    videoId: null,
}

  componentDidMount(){
    
    axios.get(`${API_URL}videos?api_key=${API_KEY}`).then(res => res.data)
    .then(data => {
      // let videoId = this.state.videoId;
     
      let videoId = data.length > 0 ? data[0].id : videoId;
      this.setState({
        videoList: data,
        videoId: videoId
        });
        }).catch(error=>{console.log(error)})
  }

  componentDidUpdate(prevProps){
           const {videoId} = this.props.match.params;
           console.log(videoId)

           const {selectedId} = this.props.match.params;
           console.log(selectedId)

           if(videoId !== prevProps.match.params.videoId) {
               this.setState({ videoId: videoId })
               console.log(videoId)
           }
  }
    
  render(){
    // const { videos } = this.props;
    
      return (
        <>
        <MainVideoDisplay videoId={this.state.videoId}/>
          <section className="videolist-container__style">
            <div className="videolist__content">
              <h2 className="nextvideo-title__style">NEXT VIDEO</h2>
              <div className="videolist__style">
                  {this.state.videoList.map((video) => {return (
                  (video.id!==this.state.videoId) ? 
                  <div key={video.id} className="nextvideo__container">
                      <div className="nextvideo__flex">
                        <NavLink to={`/${video.id}`}>
                          <img className="nextvideo__style" src={video.image} width="140" height="120" />
                        </NavLink>
                          
                        <div className="nextvideo-list-container">
                          <ul>
                            <li className="nextvideo-desc__style"
                              key={video.id}>{video.title}
                            </li>
                            <li
                            className="nextvideo-channel__style">
                              {video.channel}
                            </li>
                          </ul>
                        </div>
                      </div>
                  </div> : ("")   
                  )
                })}
              </div>
            </div>
          </section>
          </>
  )}
          }

export default VideoList
