import React from 'react';
import reactDom from 'react-dom';
import {Link} from "react-router-dom"
import "../../App.scss"
import "../VideoList/VideoList.scss"

class VideoList extends React.Component{
  constructor (props){
    super(props)
  }
  componentDidUpdate(prevProps, prevState){
    if (prevState.entry !== this.props.entry) 
    return this.setCurrentVideo()
  }

  render(){
  return (
      <section className="videolist-container__style">
        <div className="videolist__content">
          <h2 className="nextvideo-title__style">NEXT VIDEO</h2>
          <div className="videolist__style">{this.props.videoDetail.map((video) => {return (
          (video.id!==this.props.entry.id) ? 
            <div key={video.id} className="nextvideo__container">
                  <div className="nextvideo__flex">
                  <Link to="/" onClick={() => this.props.setCurrentVideo(video.id)}>
                        <video className="nextvideo__style" poster={video.image} width="140" height="120" >
                          <source className="video__style" src={video.video}/>
                        </video>
                      </Link>
                     
                      
                    <div className="nextvideo-list-container">
                      <ul>
                        <li className="nextvideo-desc__style"
                          key={video.id}>{video.title}
                        </li>
                        <li
                        className="nextvideo-channel__style">
                          {video.channel}
                        </li>
                      </ul>
                    </div>
                  </div>
              </div> : ("")   
              )
            })}
          </div>
        </div>
      </section>
  )}
          }

export default VideoList
