import React, { Component } from 'react'
import axios from "axios"
import {API_URL, API_KEY} from "../../utils/api"
import MainVideoDisplay from "../MainVideoDisplay/MainVideoDisplay"
import VideoList from "../VideoList/VideoList"

class VideoContainer extends Component {
    
    state={
        videoId: null,
        videos: []
    }

    componentDidMount(){
       
        axios.get(`${API_URL}videos?api_key=${API_KEY}`)
        .then(res => res.data)
        .then(data => {
            let videoId = this.state.videoId;
            videoId = data.length > 0 ? data[0].id : videoId;
            this.setState({videos: data, videoId: videoId})
       }).catch(error=>{console.log(error)})
    }
   
//    componentDidUpdate(prevProps){
//        const {videoId} = this.props.match.params;
//        console.log(this.props.match.params)
   
//        if(videoId !== prevProps.match.params.videoId) {
//            this.setState({ videoId: videoId })
//        }
//     }

    render() {
        return (
            <div>
                <MainVideoDisplay videoId={this.state.videoId}
                videos={this.state.videos}
                selectedVideoId={this.state.videoId}/>
                <VideoList
                  videos={this.state.videos}
                  selectedVideoId={this.state.videoId}
                />
            </div>
        )
    }
}

export default VideoContainer
