import './App.scss';
import React, {Component} from 'react';
import {BrowserRouter, Route, Switch, Redirect, Link} from "react-router-dom"
import Header from "./components/Header/Header";
import VideoList from "./components/VideoList/VideoList";
import Data from "./data/videos.json";
import DataDetails from "./data/video-details.json";
import MainVideoDisplay from "./components/MainVideoDisplay/MainVideoDisplay"
import Upload from "../src/components/Upload/Upload"
import {API_KEY, API_URL} from "../src/utils/api";
import axios from "axios";


class App extends Component{
  state = {
    // videos: Data,
    // videoDetail: DataDetails,
    mainEntry: [],
  }

  componentDidMount(){
    axios.get(`${API_URL}videos?api_key=${API_KEY}`).then(res => {this.setState({mainEntry: res})
  }).catch(error=>{console.log(error)})
  console.log(this.state)
  }

  setCurrentVideo = (id) => {
    const individualEntry = this.state.find((video) => video.id === id);
    return this.setState({ mainEntry: individualEntry});
  };

videoDisplay = (event) => {

  event.preventDefault();
  let image = this.state.image;
  let title = this.state.title;
  let channel = this.state.channel;
  let id = this.state.id;
  let description = this.state.description;
  let likes = this.state.likes;

  let timestamp= this.state.timestamp;
  let views = this.state.views;
  let video = this.state.video;
  let newVideo = { id: id, title: title, image: image, channel: channel, description: description, likes: likes, timestamp:timestamp, views: views, video: video};
  let newArray = this.state.videos.concat(newVideo);
  this.setState({ videoDetail: newArray });
  event.target.reset();
};

  render(){

  return (
    <BrowserRouter>
      <Header entry={this.videoDisplay}/>
      <Switch>

        <Route exact path="/" render={(routeProps)=> (
          <MainVideoDisplay
          entry={this.state.mainEntry}
          comments={this.state.comments}
          videoDetail={this.state.videoDetail}
          setCurrentVideo={this.setCurrentVideo}
          videos = {this.state.videos} 
          {...routeProps}
          />
        )}/>
        <Route exact path="/home/:homeId" component={MainVideoDisplay}/>
        {/* <Route exact path="/videolist" render={(routeProps)=>(
          <VideoList 
          videos = {this.state.videos} 
          videoDetail={this.state.videoDetail}
          setCurrentVideo={this.setCurrentVideo}
          entry={this.state.mainEntry}
          {...routeProps}
          />
        )}/> */}

        <Route exact path="/upload" component={Upload}/>

    <div className="App">

    </div>

    </Switch>
    </BrowserRouter>
  )}
}

export default App;
