import React from 'react'

function SelectedEntry(props) {
 
    if (props.entry===null){
        return ""
    }
    let time = new Date (props.entry.timestamp)
  
    let day = time.getUTCDate();
    let month = time.getUTCMonth() + 1;
    let year = time.getUTCFullYear();
    let newTime = `${day}/${month}/${year}`
  
    return (
        
        <div>
            
        
        <>
        <img src={props.entry.image} />
          <h3>{props.entry.title}</h3>
          <p>{props.entry.channel}</p>
          <p>{props.entry.description}</p>
        
          
          <p>{props.entry.likes}</p>
          <p>{newTime}</p>
          <p>{props.entry.views}</p>

          <section className="card-list">
                {props.entry.map((newArray) => {newArray.comments.map((comment) =>{
                <p>comment.name</p>})}
                )}
                
            </section>
      
          {/* <video>{props.entry.video}</video> */}

        </>
      
        </div>
        
    )
}

export default SelectedEntry
