import React, {Component} from 'react'
import getDate from "../../utils/GetDate"
import Comment from "../Comments/Comment"
import CommentDisplay from "../CommentDisplay/CommentDisplay"
import "../../App.scss"
import "../MainVideoDisplay/MainVideoDisplay.scss";
import Like from "../../assets/icons/Icon-likes.svg";
import Views from "../../assets/icons/Icon-views.svg";
import {API_KEY, API_URL} from "../../utils/api";
import VideoList from "../VideoList/VideoList"
import axios from "axios";
import {NavLink, withRouter} from "react-router-dom"

class MainVideoDisplay extends Component {
  constructor(props){
    super (props)
  }
    state = {
        MainVideoDisplay: null,
    }
    // "1ainjruutd1j"

    getVideoId(videoId) {
        // if(videoId == null){
        //     return false;
        //   }
        return axios.get(`${API_URL}videos/${videoId}?api_key=${API_KEY}`)
        .then(res => res.data)
        .then(data => { 
          // let videoId = this.state.mainVideoDisplay;
          // videoId = data.length > 0 ? data[0].id : videoId;
            this.setState({MainVideoDisplay: data})
    }).catch(error=>{console.log(error)})
    }
  
    componentDidMount(){   
        const { videoId }  = this.props;
        console.log(videoId)
        console.log(this.state.mainVideoDisplay)
        this.getVideoId(videoId);
    }
   
        // componentDidMount(){

        // const {videoId} = this.props.videoId;
            
        // this.getVideoId(videoId)
        // }

    componentDidUpdate(prevProps){
        //const { videoId } = this.props.match.params;
        const { videoId } = this.props.match.params;

        if(videoId !== prevProps.match.params.videoId) {
            this.getVideoId(videoId)
        }
    }
    
    render(){
    
        if (this.state.mainVideoDisplay===null){
            return ""
        }
    
        return (
            <>
                <div className="selectedentry__style">
                    <div className="flexvideo-container">
                        <video  className="video-poster__style" height="800" poster={this.state.mainVideoDisplay.image}controls><source src={this.state.mainVideoDisplay.video}/></video>
                        <div className="video-body__style">
                            <h3 className="video-title__style">{this.state.mainVideoDisplay.title}</h3>
                            <div className="mainvideo-container__style">
                                <div className="mainvideo-channel__container">
                                    <p className="video-channel__style">By {this.state.mainVideoDisplay.channel}</p>
                                    <p className="video-timestamp__style">{getDate(this.state.mainVideoDisplay.timestamp)} ago</p>
                                </div>
                                <div className="views__container">
                                    <p > <img className="views__style" src= {Views} alt="views__icon"/>{this.state.mainVideoDisplay.views}</p>
                                    <p> <img className="likes__style" src={Like} alt="likes__icon"/>{this.state.mainVideoDisplay.likes}</p>
                                </div>
                            </div>
                            <p className="video-description__style">{this.state.mainVideoDisplay.description}</p>
                    
                    <section>
                        <p className="comment__number">{this.state.mainVideoDisplay.comments.length} Comments</p>
                        <Comment />
                        <CommentDisplay entry={this.state.mainVideoDisplay} />
                    </section>
                </div>
            </div>
            
            {/* Section created solely for styling the Desktop layout purposes as content in the VideoList component. I tried using position absolute however I couldnt get the layout to stop jumping around, so i used display none   */}
            {/* <section className="pseudo-videolist-container__style">
            <div className="videolist__content">
          <h2 className="nextvideo-title__style">NEXT VIDEO</h2>
          <div className="videolist__style">
            {this.videos.map((video) => {return (
          (video.id!==this.props.videoId) ? 
            <div key={video.id} className="nextvideo__container">
                  <div className="nextvideo__flex">
                  <NavLink to={`/${video.id}`}>
                    <img className="nextvideo__style" src={video.image} width="140" height="120" />
                  </NavLink>
                     
                      
                    <div className="nextvideo-list-container">
                      <ul>
                        <li className="nextvideo-desc__style"
                          key={video.id}>{video.title}
                        </li>
                        <li
                        className="nextvideo-channel__style">
                          {video.channel}
                        </li>
                      </ul>
                    </div>
                  </div>
              </div> : ("")   
              )
            })}
          </div>
        </div>
                </section> */}
            </div>

            </>
                
            )
}
}

export default mainVideoDisplay
