import React from 'react';
import getDate from "../../utils/GetDate"
import "../../App.scss"
import "../CommentDisplay/CommentDisplay.scss"


function CommentDisplay (props){
        
    return (
        <section>
            {props.entry.comments.map((comment) =>(
                <main key={comment.id}className="comment__style">
                    <div className="comment-name__style"> 
                    <p className="card__photo"></p>{comment.name}<span className="comment-date__style">{getDate(comment.timestamp)} ago</span></div>
                    <div className="comment-body__style">        {comment.comment}</div>  
                </main>
            ))}
        </section>
    )
}

export default CommentDisplay
