import './App.scss';
import Header from "../src/Components/Header/Header";
import VideoDisplay from "../src/Components/Header/VideoDisplay/VideoDisplay";
import VideoList from "../src/Components/Header/VideoList/VideoList";
import Data from "../src/data/videos.json";
import DataDetails from "../src/data/video-details.json";
import React from 'react';
import Comment from '../src/Components/Header/Comments/Comment'
import SelectedEntry from "../src/Components/SelectedEntry/SelectedEntry"

class App extends React.Component{
  state = {
    videos: Data,
    videoDetail: DataDetails,
    selectedEntry: null,
}


setCurrentVideo = (id) => {
  const individualEntry = this.state.videoDetail.find((video) => video.id === id);
  this.setState({ selectedEntry: individualEntry });
};

videoDisplay = (event) => {
  event.preventDefault();
  let image = this.state.videoDetail.image;
  let title = this.state.videoDetail.title;
  let channel = this.state.videoDetail.channel;
  let id = this.state.videos.id;
  let description = this.state.videoDetail.description;
  let likes = this.state.videoDetail.likes;
  let time = new Date (this.state.videoDetail.timestamp)
  let day = time.getUTCDate();
  let month = time.getUTCMonth() + 1;
  let year = time.getUTCFullYear();
  let timestamp = `${day}/${month}/${year}`
  let views = this.state.videoDetail.views;
  let video = this.state.videoDetail.video;
  let newVideo = { id: id, title: title, image: image, channel: channel, description: description, likes: likes, timestamp:timestamp, views: views, video: video};
  let newArray = this.state.videos.concat(newVideo);
  this.setState({ videoDetail: newArray });
  event.target.reset();
};

  render(){
    console.log(this.state.videoDetail)
  return (
    <div className="App">
      <Header entry={this.videoDisplay}/>
      <SelectedEntry entry={this.state.selectedEntry} />
      <VideoDisplay />
      <VideoList videos = {this.state.videos} 
      videoDetail={this.state.videoDetail}
      setCurrentVideo={this.setCurrentVideo}
      />
      <Comment />

    </div>
  )}
}

export default App;
