import React from 'react'

function MainVideo(props) {
  
    if (!props.mainVideo){
        return ""
    }
    let time = new Date (props.mainVideo.timestamp)
  
    let day = time.getUTCDate();
    let month = time.getUTCMonth() + 1;
    let year = time.getUTCFullYear();
    let newTime = `${day}/${month}/${year}`
    return (
                
        <div>
        <>
        <video>src={props.mainVideo.image}</video>
          <h3>{props.mainVideo.title}</h3>
          <p>{props.mainVideo.channel}</p>
          <p>{props.mainVideo.description}</p>
          <p>{props.mainVideo.likes}</p>
          <p>{newTime}</p>
          <p>{props.mainVideo.views}</p>
          <form className="comment__form">
                    <label className= "label" htmlFor="name">JOIN THE CONVERSATION</label>
                    <img className=" avatar__style" src="" alt="avatar" />
                    <input type="name" id="name" name= "name" placeholder="Write comment here" required />
                    <button type="submit" id="comment__button">COMMENT</button>
                </form>
            {/* <p>{props.mainVideo.comments}</p> */}
          {/* <video>{props.entry.video}</video> */}

        </>
      
        </div>
    )
}

export default MainVideo
