import './App.scss';
import Header from "../src/Components/Header/Header"
import VideoDisplay from "../src/Components/Header/VideoDisplay/VideoDisplay"
import VideoList from "../src/Components/Header/VideoList/VideoList"
import Data from "../src/data/videos.json"
import React from 'react';

class App extends React.Component{
  state = {
    videos: {Data},
    selectedEntry: null,
}

clickHandler = (id) => {
  const individualEntry = this.state.videos.find((video) => video.id === id);
  this.setState({ selectedEntry: individualEntry });
};

addVideos = (event) => {
  event.preventDefault();
  let title = event.target.title.value;
  let body = event.target.body.value;
  let id = uuid();
  let newEntry = { id: id, title: title, body: body };
  let newArray = this.state.entries.concat(newEntry);
  this.setState({ entries: newArray });
  event.target.reset();
};

  render(){
  return (
    <div className="App">
      <Header />
      <VideoDisplay />
      <VideoList VideoData = {this.state.videos}/>
    </div>
  )}
}

export default App;
