import React, {Component} from 'react'
import "../../App.scss"
import "../Comments/Comment.scss"
import avatar from "../../assets/images/Mohan-muruge.jpg"
import {API_KEY, API_URL} from "../../utils/api"
import axios from "axios"


class Comment extends Component {

     submitHandler = (event, videoId) => {
        event.preventDefault()   
        event.target.reset();

        const newComments={
        comment: event.target.name.value,
        }
    
        axios.post(`${API_URL}/videos/${videoId}/comments?api_key=${API_KEY}`, newComments)
        .then((result) => console.log(result))
        .catch(error => console.log(error))
    }


    componentDidMount(){   
        const { videoId } = this.props;
       
        this.submitHandler(videoId);
      }
    

    render(){
    return (
        <div>
            <form className="comment__form" onSubmit={submitHandler}>
                <div className="comment-area__style">
                        
                    <label className= "label" htmlFor="name" >
                        JOIN THE CONVERSATION
                    </label>

                    <span>
                        <img className=" avatar__style" src= {avatar} alt="avatar" />
                        <input type="name" id="name" name= "name" placeholder="Write comment here" required />
                    </span>

                </div>
                <button type="submit" id="comment__button">COMMENT</button>
            </form>
            
        </div>
    )
}
}

export default Comment
