import React from 'react'

class SelectedEntry extends React.Component {

    render(){

    let time = new Date (entry.timestamp)
  let day = time.getUTCDate();
  let month = time.getUTCMonth() + 1;
  let year = time.getUTCFullYear();
  let newTime = `${day}/${month}/${year}`
  
    return (
        
        <div>
            
        {this.entry ? (
        <>
        <img src={this.entry.image} />
          <h3>{this.entry.title}</h3>
          <p>{this.entry.channel}</p>
          <p>{this.entry.description}</p>
          <p>{this.entry.likes}</p>
          <p>{newTime}</p>
          <p>{this.entry.views}</p>
          {/* <video>{props.entry.video}</video> */}

        </>
      ) : (
        ''
      )}
      
        </div>
        
    )
      }
}

export default SelectedEntry
