    getVideoId(videoId) {
      if(videoId == null){
          return false;
        }
      return axios.get(`${API_URL}videos/${videoId}?api_key=${API_KEY}`)
      .then(res => {
        console.log(res.data);
        // let videoId = this.state.mainVideoDisplay;
        
        this.setState({
            mainVideoDisplay: res.data, 
        })
      })
      .catch(error=>{console.log(error)})
      }
     
    componentDidMount(){   
      const { videoId } = this.props;
      console.log(videoId)
      // if(this.state.mainVideoDisplay !== videoId){
      //   this.setState({selectedVideo: videoId})}
      //   console.log(this.state.selectedVideo)

      // const selectedId = this.props;
      // console.log(selectedId)
      
      this.getVideoId(videoId);
    //   if(videoId !== this.state.videoId) {
