        // this.getVideoId(videoId)
        // }

    componentDidUpdate(prevProps){
      //const { videoId } = this.props.match.params;
      const { videoId } = this.props;
      console.log(this.props)

      if(videoId !== prevProps.videoId) {
          this.getVideoId(videoId)
          // this.setState({mainVideoDisplay: videoId})
      }
    }
    
    render(){
    
        if (this.state.mainVideoDisplay===null){
            return ""
        }
    
        return (
            <>
                <div className="selectedentry__style">
                    <div className="flexvideo-container">
                        <video  className="video-poster__style" height="800" poster={this.state.mainVideoDisplay.image}controls><source src={this.state.mainVideoDisplay.video}/></video>
                        <div className="video-body__style">
                            <h3 className="video-title__style">{this.state.mainVideoDisplay.title}</h3>
                            <div className="mainvideo-container__style">
                                <div className="mainvideo-channel__container">
                                    <p className="video-channel__style">By {this.state.mainVideoDisplay.channel}</p>
                                    <p className="video-timestamp__style">{getDate(this.state.mainVideoDisplay.timestamp)} ago</p>
                                </div>
                                <div className="views__container">
                                    <p > <img className="views__style" src= {Views} alt="views__icon"/>{this.state.mainVideoDisplay.views}</p>
                                    <p> <img className="likes__style" src={Like} alt="likes__icon"/>{this.state.mainVideoDisplay.likes}</p>
                                </div>
                            </div>
                            <p className="video-description__style">{this.state.mainVideoDisplay.description}</p>
                    
                        <section>
                          <p className="comment__number">{this.state.mainVideoDisplay.comments.length} Comments</p>
                          <Comment />
                          <CommentDisplay entry={this.state.mainVideoDisplay} />
                        </section>
                </div>
            </div>
            
            {/* Section created solely for styling the Desktop layout purposes as content in the VideoList component. I tried using position absolute however I couldnt get the layout to stop jumping around, so i used display none   */}
