import React, {Component} from 'react'
import getDate from "../../utils/GetDate"
import Comment from "../Comments/Comment"
import CommentDisplay from "../CommentDisplay/CommentDisplay"
import VideoList from "../VideoList/VideoList"
import "../../App.scss"
import "../MainVideoDisplay/MainVideoDisplay.scss"
import Like from "../../assets/icons/Icon-likes.svg"
import Views from "../../assets/icons/Icon-views.svg"
import {API_KEY, API_URL} from "../../utils/api";
import axios from "axios"


class mainEntry extends Component {
    
    state = {
        // videos: Data,
        // videoDetail: DataDetails,
        mainEntry: null,
    }
    // "1ainjruutd1j"
    getVideoId(videoId) {
        return axios.get(`${API_URL}videos/?api_key=${API_KEY}`).then(res => {console.log(res); this.setState({mainEntry: res.data[0]})
    }).catch(error=>{console.log(error)})

    }

componentDidMount(){
   const {videoId} = this.props.match.params;

   this.getVideoId(videoId)
  }

  componentDidUpdate(prevProps){
    const {videoId} = this.props.match.params;

    if(videoId !== prevProps.match.params.videoId) {
        this.getVideoId(videoId)
    }
  }
    
render(){
    if (this.state.mainEntry===null){
        return ""
    }
  
return (
    <>
        <div className="selectedentry__style">
            <div className="flexvideo-container">
                <video  className="video-poster__style" height="800" poster={this.state.mainEntry.image}controls><source src={this.state.mainEntry.video}/></video>
                <div className="video-body__style">
                    <h3 className="video-title__style">{this.state.mainEntrytitle}</h3>
                    <div className="mainvideo-container__style">
                        <div className="mainvideo-channel__container">
                            <p className="video-channel__style">By {this.state.mainEntry.channel}</p>
                            <p className="video-timestamp__style">{getDate(this.state.mainEntry.timestamp)} ago</p>
                        </div>
                        <div className="views__container">
                            <p > <img className="views__style" src= {Views} alt="views__icon"/>{this.state.mainEntryviews}</p>
                            <p> <img className="likes__style" src={Like} alt="likes__icon"/>{this.state.mainEntry.likes}</p>
                        </div>
                    </div>
                    <p className="video-description__style">{this.state.mainEntry.description}</p>
              
            <section>
                <p className="comment__number">{this.state.mainEntry.comments.length} Comments</p>
                <Comment />
                <CommentDisplay entry={this.state.mainEntry} />
                <VideoList  videos = {this.state.mainEntry.videos} 
        //   videoDetail={props.videoDetail}
        //   setCurrentVideo={props.setCurrentVideo}
          entry={this.state.mainEntry}/>
            </section>
        </div>
    </div>
      
    {/* Section created solely for styling the Desktop layout purposes as content in the VideoList component. I tried using position absolute however I couldnt get the layout to stop jumping around, so i used display none :) 
    <section className="pseudo-videolist-container__style">
        <div className="videolist__content">
                <h2 className="nextvideo-title__style">NEXT VIDEO</h2>
            <div className="videolist__style">{this.props.videoList.map((video) => {return (
            (video.id!==this.props.entry.id) ? 
                <div key={video.id} className="nextvideo__container">
                    <div className="nextvideo__flex">
                            <video className="nextvideo__style" poster={video.image} width="140" height="120" onClick={() => props.setCurrentVideo(video.id)}>
                            <source className="video__style" src={video.video}/>
                            </video>
                            <div className="nextvideo-list-container">
                                <ul>
                                    <li className="nextvideo-desc__style"
                                    key={video.id}>{video.title}
                                    </li>
                                    <li
                                    className="nextvideo-channel__style">
                                    {video.channel}
                                    </li>
                                </ul>
                            </div>
                        </div>
                    </div> : ("")
                    )
                    })}
                </div>
            </div>
        </section> */}
    </div>

    </>
        
    )
}
}

export default mainEntry
